Create Function [dbo].[ZL_StripeTrimstr](@str nvarchar(4000))
returns nvarchar(4000)
AS
begin
declare @d int;   
declare @dd int;   
declare @sumstr nvarchar(4000);   
while (Charindex('<',@str)!=0 and Charindex('>',@str)!=0)
begin      
set @d = Charindex('<',@str);   
set @dd = Charindex('>',@str);   
set @sumstr = Substring(@str,1,@d-1);   
set @str = Substring(@str,@dd+1,len(@str));  
set @str = @sumstr+@str;   
end
set @str=Replace(@str,' ','')
return @str;
end

